const calculateAvgRating = (reviews) => {
    const totalRating = reviews?.reduce((acc, item) => acc + item.rating, 0);
    const avgRating =
      totalRating === 0
        ? ''
        : totalRating === 1
        ? totalRating.toFixed(2) // Use toFixed() here
        : (totalRating / reviews?.length).toFixed(2); // Use toFixed() here

    return {
      totalRating,
      avgRating,
    };
  };
  
  export default calculateAvgRating;
